INSERT INTO `champion` VALUES ('021e87ec-9f8b-4ec1-9a26-7f831c9191f1', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Lee Sin', '/images/champions/set4-5/leesin.png', '/images/champions-bg/set4-5/leesin.png', '60', '/images/champion-skills/set4-5/leesin_godfist.png', '神拳', '主动', '{\"Damage\": \"200/375/1000\", \"Consume\": \"0/40\", \"Stun Duration\": \"2/3/10\"}', '李青', '5', '1000/1800/3240', '60/108/194.4', '51/92/165', '1', '0.85', '60', '李青对他的目标重拳出击，造成200/375/1000伤害，并将其击退到棋盘边缘，造成2/3/10秒的晕眩效果。在击退期间与目标产生碰撞的任何敌人都会受到一半伤害和1/2/10秒的晕眩效果。\r\n如果目标已经在棋盘边缘，那么这个技能会将目标从战斗中移出。\r\n如果目标仍然存活，那么李青将会向其冲刺。\r\n李青会将主要目标附近一个区域内的所有敌人都击出棋盘，并且将命中的任何敌人都击出棋盘，无论他们所在的位置。', '盲僧');
INSERT INTO `champion` VALUES ('09a6b94f-d148-43f3-b8bf-acbf4e003cf5', '2021-01-21 03:45:30', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Kindred', '/images/champions/set4-5/kindred.png', '/images/champions-bg/set4-5/kindred.png', '20', '/images/champion-skills/set4-5/kindred_danceofdread.png', '死亡之舞', '主动', '{\"Damage\": \"400/600/1000\", \"Consume\": \"0/35\"}', '千珏', '3', '650/1170/2106', '70/126/226.8', '56/101/181', '3', '0.8', '20', '狼灵扑向千珏的目标，造成400/600/1000魔法伤害并降低目标50%的治疗效果5秒。与此同时，羊灵跳离千珏的目标。', '永猎双子');
INSERT INTO `champion` VALUES ('1c8353bc-0a14-4b46-9cf2-15a5537e8e10', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Janna', '/images/champions/set4-5/janna.png', '/images/champions-bg/set4-5/janna.png', '20', '/images/champion-skills/set4-5/janna_eyeofthestorm.png', '风暴之眼', '主动', '{\"Consume\": \"40/70\", \"Shield Amount\": \"250/300/400\", \"Number of Allies\": \"2/3/4\", \"Bonus Attack Damage\": \"20/30/60\"}', '迦娜', '2', '550/990/1782', '40/72/129.6', '24/43/78', '4', '0.6', '20', '迦娜为生命值最低的2/3/4名友军提供250/300/400护盾，持续4秒。被施加了护盾的友军在此期间还会获得20/30/60攻击力。', '风暴之怒');
INSERT INTO `champion` VALUES ('2390acaf-a3cd-47b4-a032-76b99d685781', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Jax', '/images/champions/set4-5/jax.png', '/images/champions-bg/set4-5/jax.png', '30', '/images/champion-skills/set4-5/jax_counterstrike.png', '反击风暴', '主动', '{\"Damage\": \"150/250/450\", \"Consume\": \"60/110\"}', '贾克斯', '2', '750/1350/2430', '60/108/194.4', '45/81/146', '1', '0.75', '40', '贾克斯在2秒里闪避所有即将到来的攻击，随后对附近所有敌人进行打击，造成150/250/450魔法伤害和1.5秒晕眩效果。', '武器大师');
INSERT INTO `champion` VALUES ('2a29d348-f18e-4a9d-a547-04b0ef6255af', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Akali', '/images/champions/set4-5/akali.png', '/images/champions-bg/set4-5/akali.png', '20', '/images/champion-skills/set4-5/akali_fivepointstrike.png', '我流奥义！寒影', '主动', '{\"Damage\": \"175/250/400\", \"Consume\": \"0/20\"}', '阿卡丽', '3', '650/1170/2106', '55/99/178.2', '47/84/151', '1', '0.85', '20', '阿卡丽朝她目标掷出苦无，造成125/225/350魔法伤害。技能施放后会有1.25秒的法力锁定时长。', '离群之刺');
INSERT INTO `champion` VALUES ('2bb1b478-c5ed-4e87-b6e0-7ceb5b0f0905', '2021-01-21 03:45:30', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Neeko', '/images/champions/set4-5/neeko.png', '/images/champions-bg/set4-5/neeko.png', '20', '/images/champion-skills/set4-5/neeko_popblossom.png', '怒放', '主动', '{\"Damage\": \"150/225/375\", \"Consume\": \"0/70\"}', '妮蔻', '3', '700/1260/2268', '45/81/145.8', '32/57/102', '3', '0.7', '20', '妮蔻朝一名随机的目标扔出一粒种子。种子可爆炸三次，每次造成150/225/375魔法伤害。每次爆炸的影响范围都会比上一次更广。\r\n【山海绘卷】加成：第三次爆炸会获得强化，转而造成300/450/900魔法伤害。\r\n', '万花通灵');
INSERT INTO `champion` VALUES ('31d92d7a-4029-4b04-a0c7-62bf3de5abb1', '2021-01-21 03:45:30', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Azir', '/images/champions/set4-5/azir.png', '/images/champions-bg/set4-5/azir.png', '20', '/images/champion-skills/set4-5/azir_emperorsdivide.png', '禁军之墙', '主动', '{\"Consume\": \"75/150\", \"Wall Damage\": \"200/350/8888\"}', '阿兹尔', '5', '950/1710/3078', '70/126/226.8', '60/107/193', '4', '0.85', '30', '阿兹尔从他的后方召唤一道禁军之墙向前冲锋，对命中的所有敌人造成200/350/8888魔法伤害并使其减速3秒。阿兹尔近处的敌人会被击退，而远处的敌人会被击飞1.5秒。', '沙漠皇帝');
INSERT INTO `champion` VALUES ('32cf88cd-92e6-4322-8172-38c0591a9d93', '2021-01-21 03:45:31', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Tristana', '/images/champions/set4-5/tristana.png', '/images/champions-bg/set4-5/tristana.png', '40', '/images/champion-skills/set4-5/tristana_rapidfire.png', '急速射击', '主动', '{\"Consume\": \"0/40\", \"Bonus Attack Speed\": \"50%/60%/80%\", \"Bonus Magic Damage\": \"30/60/90\"}', '崔丝塔娜', '1', '500/900/1620', '50/90/162', '35/63/113', '4', '0.7', '20', '崔丝塔娜提升她50% / 60% / 80%攻击速度，并在3秒内造成额外30 / 60 / 90魔法伤害。 ', '麦林炮手');
INSERT INTO `champion` VALUES ('33c9e87e-12e6-498e-a81e-0ec79429183b', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Nasus', '/images/champions/set4-5/nasus.png', '/images/champions-bg/set4-5/nasus.png', '50', '/images/champion-skills/set4-5/nasus_wither.png', '枯萎', '主动', '{\"Damage\": \"400/600/850\", \"Consume\": \"0/60\"}', '内瑟斯', '1', '650/1170/2106', '45/81/145.8', '29/53/95', '1', '0.65', '50', '内瑟斯使相距最近的未枯萎目标变得枯萎，在 9秒里持续造成共450/900/1400魔法伤害，并在此期间使其攻击速度和移动速度减缓50%。\r\n', '沙漠死神');
INSERT INTO `champion` VALUES ('35b3a575-5a07-44e8-9e86-0b01918a36d0', '2021-01-21 03:45:31', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Tahm Kench', '/images/champions/set4-5/tahmkench.png', '/images/champions-bg/set4-5/tahmkench.png', '30', '/images/champion-skills/set4-5/tahmkench_thickskin.png', '厚实表皮', '被动', '{\"Consume\": \"Nocost\", \"Damage Reduction\": \"15/25/75\"}', '塔姆', '1', '700/1260/2268', '85/153/275.4', '43/77/138', '1', '0.5', '40', '塔姆减少15/25/75所受伤害(来自所有来源)。', '河流之主');
INSERT INTO `champion` VALUES ('37137eef-398e-48ed-8f88-c41487d5c211', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Nidalee', '/images/champions/set4-5/nidalee.png', '/images/champions-bg/set4-5/nidalee.png', '20', '/images/champion-skills/set4-5/nidalee_javelintoss.png', '标枪投掷', '主动', '{\"Consume\": \"0/70\", \"Base Damage\": \"225/350/600\"}', '奈德丽', '1', '550/990/1782', '45/81/145.8', '32/57/102', '4', '0.7', '30', '奈德丽朝相距最远的敌人投掷她的标枪，标枪造成225/350/600魔法伤害，并且每飞行一格就会使这个伤害值提升20%', '狂野女猎手');
INSERT INTO `champion` VALUES ('3b3a60ef-1141-4bce-9664-d27c8da8f9b8', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Tryndamere', '/images/champions/set4-5/tryndamere.png', '/images/champions-bg/set4-5/tryndamere.png', '60', '/images/champion-skills/set4-5/tryndamere_spinningslash.png', '旋风斩', '主动', '{\"Consume\": \"50/100\", \"Spin Damage\": \"140%/140%/140%\", \"Bonus Damage\": \"75%/100%/200%\"}', '泰达米尔', '4', '850/1530/2754', '85/153/275.4', '72/130/234', '1', '0.85', '60', '泰达米尔朝着拥有最多敌人的一条直线进行旋风斩，对沿途的敌人们造成他140%攻击力的伤害，并使他的下三次普通攻击造成75/100/200%额外伤害。', '蛮族之王');
INSERT INTO `champion` VALUES ('4010c937-2fa0-417e-88a1-13611e903929', '2021-01-21 03:45:30', '2021-01-30 06:42:13', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Fiora', '/images/champions/set4-5/fiora.png', '/images/champions-bg/set4-5/fiora.png', '30', '/images/champion-skills/set4-5/fiora_riposte.png', '劳伦特心眼刀', '主动', '{\"Damage\": \"250/400/600\", \"Consume\": \"0/75\", \"Stun Duration\": \"2/2.5/4\"}', '菲奥娜', '1', '600/1080/1944', '45/81/145.8', '34/61/109', '1', '0.75', '35', '菲奥娜进入防御姿态1.5秒，使她变得免疫伤害和敌方的技能效果。随后，她会进行还击，对附近的一名敌人造成250/400/600魔法伤害和 2/2.5/4秒晕眩效果。', '无双剑姬');
INSERT INTO `champion` VALUES ('4315fe20-bcc9-4e2a-8253-25a8d18b6723', '2021-01-21 03:45:32', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Vi', '/images/champions/set4-5/vi.png', '/images/champions-bg/set4-5/vi.png', '30', '/images/champion-skills/set4-5/vi_dentingblow.png', '爆弹重拳', '主动', '{\"Damage\": \"250/400/800\", \"Consume\": \"0/50\", \"Armor Reduction\": \"40%/60%/80%\"}', '蔚', '2', '700/1260/2268', '65/117/210.6', '39/70/126', '1', '0.6', '40', '蔚的攻击会震透她的目标，对锥形范围内的所有敌人造成250/400/800魔法伤害并降低他们40/60/80%护甲，持续8秒。', '皮城执法官');
INSERT INTO `champion` VALUES ('4557a2e4-92d8-47a1-b054-3039bc5269f5', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Shyvana', '/images/champions/set4-5/shyvana.png', '/images/champions-bg/set4-5/shyvana.png', '80', '/images/champion-skills/set4-5/shyvana_dragonsdescent.png', '魔龙降临', '主动', '{\"Damage\": \"150/300/600\", \"Consume\": \"0/75\", \"Bonus Health\": \"40%/45%/50%\", \"Attack Damage while Dragon\": \"30/60/120\"}', '希瓦娜', '3', '750/1350/2430', '70/126/226.8', '53/95/170', '1', '0.75', '35', '希瓦娜从她的当前目标处跳离2格，然后变形为远程攻击的龙形态，在龙形态下，希瓦娜获得40/45/50%额外生命值，30/60/120攻击力，她的普通攻击变为远程并且会燃烧目标2秒，共造成150/300/600魔法伤害。\r\n', '龙血武姬 ');
INSERT INTO `champion` VALUES ('54cbc7ce-34f2-41e0-b895-40c1a0c641d5', '2021-01-21 03:45:30', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Kalista', '/images/champions/set4-5/kalista.png', '/images/champions-bg/set4-5/kalista.png', '20', '/images/champion-skills/set4-5/kalista_rend.png', '撕裂', '被动', '{\"Consume\": \"Nocost\", \"Maximum Health Damage\": \"4%/6%/9%\"}', '卡莉丝塔', '3', '650/1170/2106', '65/117/210.6', '65/117/211', '3', '1', '20', '被动:卡莉丝塔的长矛会留在她的目标身上，每根长矛在拔出时会造成相当于目标4/6/9%大生命值的魔法伤害。卡莉斯塔会在目标身上的长矛数量足以致命时将它们全部拔出。', '复仇之矛');
INSERT INTO `champion` VALUES ('57bb3ed6-c9bb-4b55-894c-53a12e7cf481', '2021-01-21 03:45:32', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Zilean', '/images/champions/set4-5/zilean.png', '/images/champions-bg/set4-5/zilean.png', '20', '/images/champion-skills/set4-5/zilean_rewindfate.png', '命运倒行', '主动', '{\"Consume\": \"100/150\", \"Revive Delay\": \"3.5/3/1\", \"Health Restored\": \"350/500/3000\", \"Number of Targets\": \"2/3/10\", \"Bonus Attack Speed\": \"50%/75%/500%\"}', '基兰', '5', '750/1350/2430', '45/81/145.8', '34/61/109', '3', '0.75', '20', '基兰为生命值最低的2/3/10名友军放置一个保护性的时光神符。在他们死亡时，他们会在3.5/3/1秒后复活，以拥有350/500/3000生命值且移除了所有负面效果的状态重返战斗。在复活之后，他们会获得50%额外攻击速度，持续到战斗结束为止。', '时光守护者');
INSERT INTO `champion` VALUES ('58ff8067-9676-41e2-963e-bdd06ed1a5e3', '2021-01-21 03:45:32', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Zed', '/images/champions/set4-5/zed.png', '/images/champions-bg/set4-5/zed.png', '20', '/images/champion-skills/set4-5/zed_contemptfortheweak.png', '影忍法！灭魂劫', '被动', '{\"Damage\": \"25/50/75\", \"Consume\": \"Nocost\", \"Attack Damage Stolen\": \"20%/30%/40%\"}', '劫', '2', '600/1080/1944', '55/99/178.2', '44/79/143', '1', '0.8', '20', '固有能力：在战斗环节开始时，劫会传送到敌方后排。\r\n被动：每第三次攻击，劫会造成25/50/75额外魔法伤害并窃取目标20/25/30%的当前攻击力。', '影流之主');
INSERT INTO `champion` VALUES ('59eb0921-e55a-431c-8ee0-698a6a6991e5', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Pyke', '/images/champions/set4-5/pyke.png', '/images/champions-bg/set4-5/pyke.png', '30', '/images/champion-skills/set4-5/pyke_phantomundertow.png', '魅影浪洄', '主动', '{\"Damage\": \"100/175/275\", \"Consume\": \"60/120\", \"Stun Duration\": \"2/2.5/4\"}', '派克', '2', '700/1260/2268', '50/90/162', '30/54/97', '1', '0.6', '50', '派克在他的位置上留下一道残影，随后冲到距离最远的敌人身后。在1秒后，他的残影会折返至派克处，对沿途的所有敌人造成100/175/275魔法伤害和2/2.5/4秒晕眩效果。', '血港鬼影');
INSERT INTO `champion` VALUES ('5e9bd376-e743-4d15-86fa-e2c45ddb9d23', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'ChoGath', '/images/champions/set4-5/chogath.png', '/images/champions-bg/set4-5/chogath.png', '40', '/images/champion-skills/set4-5/chogath_rupture.png', '破裂', '主动', '{\"Damage\": \"200/400/1500\", \"Consume\": \"100/180\", \"Knockup Duration\": \"2/2.5/8\"}', '科加斯', '4', '1400/2520/4536', '100/180/324', '60/108/194', '1', '0.6', '40', '科加斯碎裂一个大型区域，造成200/400/1500魔法伤害并将区域内的所有敌人击飞2/2.5/8秒。\r\n【山海绘卷】加成：科加斯击飞战场上的所有敌人。', '虚空恐惧');
INSERT INTO `champion` VALUES ('64798b72-4de9-4a3f-a5cf-3bb6dd1e00e9', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Morgana', '/images/champions/set4-5/morgana.png', '/images/champions-bg/set4-5/morgana.png', '60', '/images/champion-skills/set4-5/morgana_hallowedground.png', '圣化之地', '主动', '{\"Damage\": \"325/525/2000\", \"Consume\": \"60/120\", \"AD Reduction\": \"40%/40%/80%\"}', '莫甘娜', '4', '750/1350/2430', '50/90/162', '35/63/113', '3', '0.7', '60', '莫甘娜圣化她目标下方的地面，在5秒里对其中的敌人持续造成共250/400/2000魔法伤害并击碎他们40%魔法抗性，除非他们离开该区域。\r\n技能不再基于实际伤害值来治疗莫甘娜。她的技能现在会使敌人们在处于范围中时降低40%攻击力。不再击碎魔法抗性\r\n攻击距离：2格', '堕落天使');
INSERT INTO `champion` VALUES ('7000fec6-38ac-4505-a7a0-23de5f5eb399', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Kennen', '/images/champions/set4-5/kennen.png', '/images/champions-bg/set4-5/kennen.png', '30', '/images/champion-skills/set4-5/kennen_slicingmaelstrom.png', '秘奥义！万雷天牢引', '主动', '{\"Consume\": \"50/125\", \"Total Damage\": \"300/450/1200\"}', '凯南', '3', '650/1170/2106', '60/108/194.4', '39/70/126', '2', '0.65', '30', '凯南召唤一团风暴环绕自身3秒，每0.5秒对区域内的所有敌人降下闪电箭，并在期间内持续造成共300/450/1200魔法伤害。被3道闪电箭击中的敌人会被晕眩1.5秒。', '狂暴之心');
INSERT INTO `champion` VALUES ('74910d79-fbd5-4873-9642-efc02a8edb2a', '2021-01-21 03:45:32', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Wukong', '/images/champions/set4-5/wukong.png', '/images/champions-bg/set4-5/wukong.png', '30', '/images/champion-skills/set4-5/wukong_crushingblow.png', '粉碎打击', '主动', '{\"Consume\": \"0/50\", \"Stun Duration\": \"1.5/2/3\", \"Percent of Attack Damage\": \"250%/265%/280%\"}', '孙悟空', '1', '650/1170/2106', '55/99/178.2', '36/64/116', '1', '0.65', '40', '孙悟空用金箍棒猛砸他的目标，造成相当于他250/265/280%攻击力的物理伤害和1.5/1.75/2秒晕眩效果。', '齐天大圣');
INSERT INTO `champion` VALUES ('76bdb7e6-d79a-4456-95fc-b5a49ddb9693', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Talon', '/images/champions/set4-5/talon.png', '/images/champions-bg/set4-5/talon.png', '20', '/images/champion-skills/set4-5/talon_truestrike.png', '真意击', '主动', '{\"Consume\": \"0/40\", \"Bonus Damage\": \"85/135/400\", \"Percent of Attack Damage\": \"240%/250%/275%\"}', '泰隆', '4', '800/1440/2592', '90/162/291.6', '72/130/233', '1', '0.8', '30', '泰隆戳刺他的目标，造成240/250/275%攻击力外加85/135/400额外物理伤害。如果这个技能击杀了目标，那么泰隆会变得不可阻挡、无法被选取，同时跃向这一回合中护甲最低的敌人。\r\n修复了一个BUG，该BUG曾导致：【泰隆】的技能的额外伤害无法暴击\r\n【泰隆】的跳跃已略微提速\r\n', '刀锋之影');
INSERT INTO `champion` VALUES ('801d7b12-1c69-48f3-b8cc-406be958b9f5', '2021-01-21 03:45:30', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Diana', '/images/champions/set4-5/diana.png', '/images/champions-bg/set4-5/diana.png', '20', '/images/champion-skills/set4-5/diana_palecascade.png', '苍白之瀑', '主动', '{\"Orbs\": \"4/5/7\", \"Damage\": \"90/100/110\", \"Consume\": \"0/70\", \"Shield Amount\": \"200/300/450\"}', '黛安娜', '1', '550/990/1782', '50/90/162', '35/63/113', '1', '0.7', '30', '黛安娜获得一层 200/300/450生命值的护盾，持续4秒，并召唤4/5/6颗护体法球。这些法球会在接触到敌人时爆炸，造成 90/100/110魔法伤害，并在最后一颗法球爆炸后刷新黛安娜的护盾。', '皎月女神');
INSERT INTO `champion` VALUES ('809f656c-3611-4353-832f-75ef3ab052b7', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Shen', '/images/champions/set4-5/shen.png', '/images/champions-bg/set4-5/shen.png', '40', '/images/champion-skills/set4-5/shen_shadowdash.png', '奥义！影缚', '主动', '{\"Consume\": \"50/100\", \"Duration\": \"4/4/8\", \"Shield Amount\": \"500/800/4000\"}', '慎', '4', '950/1710/3078', '60/108/194.4', '39/70/126', '1', '0.65', '60', '慎冲刺越过他的目标，获得一层500/800/4000生命值的护盾，并嘲讽邻格的所有敌人3秒。', '暮光之眼');
INSERT INTO `champion` VALUES ('8281fa6d-eae8-4cdf-96b4-4c0dcbe4d4d1', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Garen', '/images/champions/set4-5/garen.png', '/images/champions-bg/set4-5/garen.png', '35', '/images/champion-skills/set4-5/garen_judgement.png', '审判', '主动', '{\"Consume\": \"0/100\", \"Total Damage\": \"450/675/1125\"}', '盖伦', '1', '600/1080/1944', '50/90/162', '30/54/97', '1', '0.6', '50', '盖伦旋转他的大剑4秒，在持续期间对邻近敌人造成共450/675/1125魔法伤害并使即将到来的魔法伤害降低80%。', '德玛西亚之力');
INSERT INTO `champion` VALUES ('9086b7da-8a96-48c3-aaf5-b435a6485d8a', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Brand', '/images/champions/set4-5/brand.png', '/images/champions-bg/set4-5/brand.png', '40', '/images/champion-skills/set4-5/brand_dragonfirepillar.png', '龙焰之柱', '主动', '{\"Damage\": \"200/300/500\", \"Consume\": \"0/60\", \"Stun Duration\": \"1.5/2/3\"}', '布兰德', '1', '500/900/1620', '40/72/129.6', '26/47/84', '3', '0.65', '20', '在短暂的延迟后，布兰德会在拥有最高攻速的敌人脚下引发一道烈焰，对其中的敌人们造成200/300/500魔法伤害。如果有敌人站在其中心区域，那么该敌人还会被晕眩1.5/2/3秒。\r\n', '复仇焰魂');
INSERT INTO `champion` VALUES ('94278cf0-7ff8-40bc-8ca1-df4faea3c681', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Ornn', '/images/champions/set4-5/ornn.png', '/images/champions-bg/set4-5/ornn.png', '50', '/images/champion-skills/set4-5/ornn_stampede.png', '元素暴走', '主动', '{\"Damage\": \"150/250/750\", \"Consume\": \"80/160\"}', '奥恩', '5', '900/1620/2916', '100/180/324', '55/99/178', '1', '0.55', '50', '奥恩从相距最远的敌人身后召唤出一个元素灵体来疾奔向他，使命中的敌人们减少50%攻击速度，持续150/250/750魔法伤害。\r\n如果元素灵体冲到奥恩所在位置，那么奥恩会对它进行头槌，来将它重新导向至另一名远处的敌人，造成1.5/2/15秒晕眩和150/250/750魔法伤害。', '山隐之焰');
INSERT INTO `champion` VALUES ('94cd3ae5-8608-4513-a57c-63f49810e504', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Irelia', '/images/champions/set4-5/irelia.png', '/images/champions-bg/set4-5/irelia.png', '40', '/images/champion-skills/set4-5/irelia_bladestorm.png', '剑刃风暴', '主动', '{\"Damage\": \"200/300/550\", \"Consume\": \"50/100\", \"Disarm Duration\": \"2.5/3/3.5\"}', '艾瑞莉娅', '3', '800/1440/2592', '55/99/178.2', '36/64/116', '1', '0.65', '40', '艾瑞莉娅朝她前方释放出5团剑刃风暴，造成200/300/600魔法伤害，施加攻击特效并缴械敌人2.5/3/3.5秒。', '刀锋舞者');
INSERT INTO `champion` VALUES ('9676da61-661c-4414-8723-ca004a22160d', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Katarina', '/images/champions/set4-5/katarina.png', '/images/champions-bg/set4-5/katarina.png', '20', '/images/champion-skills/set4-5/katarina_deathlotus.png', '死亡莲华', '主动', '{\"Consume\": \"0/90\", \"Total Damage\": \"600/900/1650\", \"Number of Targets\": \"4/6/8\"}', '卡特琳娜', '3', '500/900/1620', '65/117/210.6', '42/76/137', '1', '0.65', '20', '卡特琳娜引导2.5秒，朝附近4/6/8名敌人投掷匕首，在此期间持续造成共600/900/1650魔法伤害，并使他的目标们获得的治疗效果减少50%，持续5秒。', '不祥之刃');
INSERT INTO `champion` VALUES ('a1d8debc-350f-48d9-9909-288dc00d1b10', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Elise', '/images/champions/set4-5/elise.png', '/images/champions-bg/set4-5/elise.png', '20', '/images/champion-skills/set4-5/elise_spiderform.png', '蜘蛛形态', '主动', '{\"Consume\": \"0/75\", \"Bonus Health\": \"35%/40%/45%\", \"Health on Hit\": \"40/80/160\"}', '伊莉丝', '1', '600/1080/1944', '45/81/145.8', '29/53/95', '2', '0.65', '30', '伊莉丝变形为一只蜘蛛，获得35/40/45%最大生命值并使她的每次攻击可以回复40/80/160生命值。', '蜘蛛女皇');
INSERT INTO `champion` VALUES ('a73d011e-c7f5-4f8f-9949-66fc8d55b93b', '2021-01-21 03:45:31', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Sivir', '/images/champions/set4-5/sivir.png', '/images/champions-bg/set4-5/sivir.png', '20', '/images/champion-skills/set4-5/sivir_onthehunt.png', '狩猎', '主动', '{\"Consume\": \"0/70\", \"Attack Speed\": \"40%/50%/70%\", \"Attack Damage\": \"100/200/400\"}', '希维尔', '3', '650/1170/2106', '70/126/226.8', '60/107/193', '3', '0.85', '20', '希维尔振奋2格内的友军们，为其提供持续5秒的40/50/70%攻击速度。在此期间，希维尔会获得100/200/400额外攻击力。\r\n', '战争女神');
INSERT INTO `champion` VALUES ('ad46fe80-0d60-47f9-8c8a-ec0167a82dd3', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Yone', '/images/champions/set4-5/yone.png', '/images/champions-bg/set4-5/yone.png', '40', '/images/champion-skills/set4-5/yone_sealfate.png', '封命绝运斩', '主动', '{\"Damage\": \"600/1200/9999\", \"Consume\": \"0/60\", \"Unforgotten Base Damage\": \"350/600/1500\"}', '永恩', '5', '950/1710/3078', '80/144/259.2', '76/137/246', '1', '0.95', '40', '永恩对他前方的一条直线进行戳刺，造成600/1200/9999魔法伤害(由命中的所有敌人分摊)和1.5秒击飞效果。永恩会对命中的敌人们施加死亡标记，降低他们60%的护甲和魔法抗性。\r\n随后，永恩的技能会替换为【封魔剑魂】，直到场上没有带着死亡标记的敌人。\r\n【封魔剑魂】不再造成基于已损失生命值的伤害。作为替代，【封魔剑魂】将基于目标的已损失生命值而造成额外的百分比伤害，至多造成100%额外伤害\r\n【永恩】在施放技能期间会变为不可被选取状态\r\n【封尘绝念斩】的削减效果和【封魔剑魂】的处决标记现在都仅持续8秒', '封魔剑魂');
INSERT INTO `champion` VALUES ('ae54a2fe-d355-40e1-9ffc-5a4e678f2108', '2021-01-21 03:45:29', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Braum', '/images/champions/set4-5/braum.png', '/images/champions-bg/set4-5/braum.png', '60', '/images/champion-skills/set4-5/braum_unbreakable.png', '坚不可摧', '主动', '{\"Consume\": \"30/70\", \"Damage Reduction\": \"75%/80%/90%\"}', '布隆', '2', '800/1440/2592', '40/72/129.6', '24/43/78', '1', '0.6', '60', '布隆面朝相距最远的敌人并举起他的盾牌4秒，吸收并拦截所有即将到来的弹体，并使来自该方向上的伤害减少75/80/90%。\r\n', '弗雷尔卓德之心');
INSERT INTO `champion` VALUES ('b9788725-6c04-42cb-9af4-3a6d636d8b0a', '2021-01-21 03:45:30', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Aatrox', '/images/champions/set4-5/aatrox.png', '/images/champions-bg/set4-5/aatrox.png', '40', '/images/champion-skills/set4-5/aatrox_infernalchains.png', '恶火束链', '主动', '{\"Damage\": \"350/550/1500\", \"Consume\": \"75/150\", \"Number of Targets\": \"2/2/5\"}', '亚托克斯', '4', '850/1530/2754', '60/108/194.4', '39/70/126', '1', '0.65', '70', '亚托克斯会将相距最远的2/2/5名敌人拽向他，然后猛击前方的地面，对命中的所有敌人造成350/550/1500伤害。', '暗裔剑魔');
INSERT INTO `champion` VALUES ('c06b1f39-a72d-4e5d-b00f-0866ac8e7d5d', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Nautilus', '/images/champions/set4-5/nautilus.png', '/images/champions-bg/set4-5/nautilus.png', '30', '/images/champion-skills/set4-5/nautilus_impactcrater.png', '陨石坑', '主动', '{\"Damage\": \"300/450/1000\", \"Consume\": \"75/150\", \"Stun Duration\": \"3/4/5\"}', '诺提勒斯', '2', '750/1350/2430', '55/99/178.2', '33/59/107', '1', '0.6', '45', '诺提勒斯使他目标脚下的地面喷发，将其击飞并使其晕眩3/4/5秒。它也会对沿途的所有敌人造成300/450/1000魔法伤害。\r\n【山海绘卷】加成：诺提勒斯还会为自身提供一个护盾，格挡60%即将到来的伤害，持续5秒。', '深海泰坦');
INSERT INTO `champion` VALUES ('c14d3198-3d2d-41f4-ae5b-f5b185d488f8', '2021-01-21 03:45:32', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Yasuo', '/images/champions/set4-5/yasuo.png', '/images/champions-bg/set4-5/yasuo.png', '20', '/images/champion-skills/set4-5/yasuo_strikingsteel.png', '斩钢闪', '主动', '{\"Consume\": \"0/45\", \"Percent of Attack Damage\": \"180%/200%/225%\"}', '亚索', '1', '550/990/1782', '50/90/162', '35/63/113', '1', '0.7', '30', '亚索朝着前方2格进行戳刺，视为一次拥有180/200/225%攻击力的攻击。', '疾风剑豪');
INSERT INTO `champion` VALUES ('c2552522-b938-4eca-a924-e061f814fc1f', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Jarvan IV', '/images/champions/set4-5/jarvaniv.png', '/images/champions-bg/set4-5/jarvaniv.png', '30', '/images/champion-skills/set4-5/jarvaniv_dragonstrike.png', '巨龙撞击', '主动', '{\"Damage\": \"175/250/750\", \"Consume\": \"50/100\"}', '嘉文四世', '2', '600/1080/1944', '60/108/194.4', '36/65/117', '1', '0.6', '45', '嘉文四世将他的军旗投掷到相距最远的敌人附近，随后用他的长矛将他自身拉向该敌人，对沿途的敌人造成175/250/750魔法伤害、击飞效果和1秒晕眩效果。', '德玛西亚皇子');
INSERT INTO `champion` VALUES ('c7df6218-a6e0-448a-aea4-38b33b4972c1', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Sett', '/images/champions/set4-5/sett.png', '/images/champions-bg/set4-5/sett.png', '30', '/images/champion-skills/set4-5/sett_showstopper.png', '叹为观止', '主动', '{\"Consume\": \"100/175\", \"Primary Damage %\": \"40%/60%/400%\", \"Secondary Damage %\": \"20%/30%/200%\"}', '瑟提', '5', '1100/1980/3564', '60/108/194.4', '45/81/146', '1', '0.75', '60', '瑟提抓住他的目标然后向前猛击，对目标造成目标40/60/400%最大生命值的魔法伤害，并对其周围大片区域内的所有其它敌人造成该目标15/25/200%最大生命值的魔法伤害。', '腕豪');
INSERT INTO `champion` VALUES ('c828c64f-6df7-4ab5-97d5-1b8c4d2289a8', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Nunu', '/images/champions/set4-5/nunu.png', '/images/champions-bg/set4-5/nunu.png', '30', '/images/champion-skills/set4-5/nunu_consume.png', '吞噬', '主动', '{\"Damage\": \"450/650/1800\", \"Consume\": \"0/85\"}', '努努和威朗普', '3', '750/1350/2430', '65/117/210.6', '36/64/116', '1', '0.55', '50', '威朗普撕咬他的目标，造成 450/650/1800魔法伤害。如果目标在被撕咬前的生命值低于威朗普，那么威朗普会造成额外的50%伤害并且变成真实伤害。', '雪原双子');
INSERT INTO `champion` VALUES ('cbf85f77-9076-4493-b28a-5417176f1806', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Swain', '/images/champions/set4-5/swain.png', '/images/champions-bg/set4-5/swain.png', '80', '/images/champion-skills/set4-5/swain_draconicascension.png', '魔龙升华', '主动', '{\"Consume\": \"40/100\", \"Hex Range\": \"2.5/2.5/5\", \"Cone Damage\": \"175/350/5000\", \"Health Increase\": \"60%/75%/100%\", \"Damage Per Second\": \"75/150/1000\"}', '斯维因', '5', '1000/1800/3240', '60/108/194.4', '39/70/126', '2', '0.65', '60', '斯维因变形成龙形态，持续6秒，获得60/75/100%最大生命值并且每秒对2.5/2.5/5格内的敌人们造成75/150/1000魔法伤害。\r\n在龙形态下，斯维因每2秒就会对他前方的锥形范围进行烈焰吐息，造成175/350/5000魔法伤害，并在10秒里持续灼烧敌人们，共造成相当于他们25%最大生命值的真实伤害，并在此期间降低50%治疗效果。', '诺克萨斯统领');
INSERT INTO `champion` VALUES ('ccbe568e-7d45-44d8-9e2e-31d7e9ddb8dd', '2021-01-21 03:45:32', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'TwistedFate', '/images/champions/set4-5/twistedfate.png', '/images/champions-bg/set4-5/twistedfate.png', '20', '/images/champion-skills/set4-5/twistedfate_wildcards.png', '万能牌', '主动', '{\"Damage\": \"250/350/475\", \"Consume\": \"0/70\"}', '崔斯特', '1', '550/990/1782', '45/81/145.8', '29/53/95', '3', '0.65', '20', '崔斯特沿锥形掷出三张牌，对沿途的每个敌人造成200/350/525魔法伤害。', '卡牌大师');
INSERT INTO `champion` VALUES ('ce087071-6b41-490f-98d4-9d86c3ef54d8', '2021-01-21 03:45:30', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Maokai', '/images/champions/set4-5/maokai.png', '/images/champions-bg/set4-5/maokai.png', '30', '/images/champion-skills/set4-5/maokai_bramblesmash.png', '荆棘重击', '被动', '{\"Damage\": \"225/350/550\", \"Consume\": \"0/75\"}', '茂凯', '1', '700/1260/2268', '55/99/178.2', '28/50/89', '1', '0.5', '40', '茂凯猛击地面，朝前方引发一道冲击波，造成225/350/550魔法伤害并减少50%攻击速度', '扭曲树精');
INSERT INTO `champion` VALUES ('d02830e7-3f13-4a80-bdce-83a2812f7e87', '2021-01-21 03:45:30', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Annie', '/images/champions/set4-5/annie.png', '/images/champions-bg/set4-5/annie.png', '40', '/images/champion-skills/set4-5/annie_burstshield.png', '爆裂护盾', '主动', '{\"Damage\": \"250/350/450\", \"Consume\": \"0/65\", \"Shield Amount\": \"500/700/900\"}', '安妮', '2', '750/1350/2430', '45/81/145.8', '27/49/87', '2', '0.6', '40', '安妮用火焰引爆一个锥形区域，对她前方的敌人造成250/350/450魔法伤害，然后给自己生成一个拥有500/700/900生命值的护盾，持续8秒。', '黑暗之女');
INSERT INTO `champion` VALUES ('d5a7c66a-ecb5-4e73-a29c-2c6c32f02fe0', '2021-01-21 03:45:30', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Kayle', '/images/champions/set4-5/kayle.png', '/images/champions-bg/set4-5/kayle.png', '20', '/images/champion-skills/set4-5/kayle_divineascension.png', '登神', '主动', '{\"Damage\": \"100/180/500\", \"Consume\": \"0/60\"}', '凯尔', '4', '650/1170/2106', '75/135/243', '68/122/219', '3', '0.9', '20', '凯尔登上神阶，使她的攻击可以附带焰浪，造成100/180/500额外魔法伤害。\r\n', '正义天使');
INSERT INTO `champion` VALUES ('d68b5577-f45f-48e2-bdc6-6d056d388988', '2021-01-21 03:45:31', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Olaf', '/images/champions/set4-5/olaf.png', '/images/champions-bg/set4-5/olaf.png', '60', '/images/champion-skills/set4-5/olaf_ragnarok.png', '诸神黄昏', '主动', '{\"Consume\": \"0/70\", \"Attack Speed\": \"150%/175%/350%\", \"% Cleave Damage\": \"40%/45%/50%\"}', '奥拉夫', '4', '900/1620/2916', '80/144/259.2', '64/115/207', '1', '0.8', '60', '奥拉夫获得150/175/350%攻击速度，免疫控制效果，并且他的攻击会顺劈他前方一个锥形区域来造成40/45/50%物理伤害，持续到战斗环节结束为止。\r\n', '狂战士');
INSERT INTO `champion` VALUES ('d7bb1bdf-96ab-4900-acf1-a44237572fdd', '2021-01-21 03:45:31', '2021-01-30 06:42:12', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Veigar', '/images/champions/set4-5/veigar.png', '/images/champions-bg/set4-5/veigar.png', '20', '/images/champion-skills/set4-5/veigar_darkblossom.png', '暗黑绽放', '主动', '{\"Damage\": \"450/600/900\", \"Consume\": \"0/55\", \"SP Gain On Kill\": \"1/1/2\"}', '维迦', '3', '600/1080/1944', '40/72/129.6', '26/47/84', '3', '0.65', '20', '维迦引爆生命值最低的敌人，造成450/600/900魔法伤害。\r\n如果目标被这个技能击杀，那么维迦会永久获得1/1/2法术强度。', '邪恶小法师');
INSERT INTO `champion` VALUES ('e0182a73-6527-457d-a6fe-dbdc0610c668', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Samira', '/images/champions/set4-5/samira.png', '/images/champions-bg/set4-5/samira.png', '30', '/images/champion-skills/set4-5/samira_infernotrigger.png', '炼狱扳机', '主动', '{\"Consume\": \"Nocost\", \"Shots Per Second\": \"3/4/10\", \"Number of Targets\": \"3/3/9\", \"Percent of Attack Damage\": \"50%/60%/80%\"}', '莎弥拉', '5', '900/1620/2916', '65/117/210.6', '55/99/179', '3', '0.85', '30', '莎弥拉冲刺并变为不可阻挡状态，每秒对2.5格内的3名敌人释放3/3/10颗子弹，持续2秒。\r\n每颗子弹造成相当于她50/60/100%攻击力的伤害。持续期间，莎弥拉获得100%闪避几率。', '沙漠玫瑰');
INSERT INTO `champion` VALUES ('e133b143-aa76-44a5-8e4b-6ecd248a9605', '2021-01-21 03:45:30', '2021-01-30 06:42:13', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Darius', '/images/champions/set4-5/darius.png', '/images/champions-bg/set4-5/darius.png', '60', '/images/champion-skills/set4-5/darius_fortunesguillotine.png', '福星拜年式', '主动', '{\"Damage\": \"550/800/1300\", \"Consume\": \"0/50\"}', '德莱厄斯', '3', '800/1440/2592', '70/126/226.8', '49/88/159', '1', '0.7', '60', '德莱厄斯暴扣一名敌人，造成550/800/1300魔法伤害。在暴扣时，德莱厄斯处于不可阻挡状态。如果这个技能击杀了目标，那么德莱厄斯会立刻再次施放它，但造成的伤害降低25%。\r\n', '诺克萨斯之手');
INSERT INTO `champion` VALUES ('e5645b98-64a6-40b7-8a67-d42f54d660f6', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Yuumi', '/images/champions/set4-5/yuumi.png', '/images/champions-bg/set4-5/yuumi.png', '20', '/images/champion-skills/set4-5/yuumi_zoomies.png', '旺盛精力', '主动', '{\"Consume\": \"0/40\", \"Healing\": \"30%/45%/75%\", \"Attack Speed\": \"30%/40%/60%\"}', '悠米', '3', '650/1170/2106', '40/72/129.6', '24/43/78', '3', '0.6', '20', '悠米冲到生命值最低的友军处，为其回复30/45/75%已损失生命值并为其提供30/40/60%攻击速度，持续5秒。随后她会对着相距最远的友军重复这个过程。', '魔法猫咪');
INSERT INTO `champion` VALUES ('e56d5064-a591-4773-86b3-54557273a554', '2021-01-21 03:45:31', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Vladimir', '/images/champions/set4-5/vladimir.png', '/images/champions-bg/set4-5/vladimir.png', '40', '/images/champion-skills/set4-5/vladimir_transfusion.png', '鲜血转换', '主动', '{\"Damage\": \"400/600/900\", \"Consume\": \"0/60\"}', '弗拉基米尔', '2', '750/1350/2430', '40/72/129.6', '24/43/78', '2', '0.6', '40', '弗拉基米尔对他的目标造成400/600/900魔法伤害。所有附近的友方英雄会获得50%实际伤害值的治疗效果。', '猩红收割者');
INSERT INTO `champion` VALUES ('e89ab1c1-d8f0-4e26-9067-87fceddcc1a9', '2021-01-21 03:45:31', '2021-01-30 06:42:13', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Lulu', '/images/champions/set4-5/lulu.png', '/images/champions-bg/set4-5/lulu.png', '20', '/images/champion-skills/set4-5/lulu_wildgrowth.png', '狂野生长', '主动', '{\"Consume\": \"80/120\", \"Bonus Health\": \"400/600/950\"}', '璐璐', '2', '500/900/1620', '50/90/162', '30/54/97', '3', '0.6', '20', '璐璐使一名低生命值的友军巨大化，为其提供400/600/950额外生命值并将其附近的敌人击飞1秒。\r\n【璐璐】技能时长：持续到战斗环节结束\r\n【璐璐】在一个友方英雄身上再次施放她的技能时，会正确地为其提供相当于该技能额外生命值数额的治疗效果并触发击飞', '仙灵女巫');
INSERT INTO `champion` VALUES ('f3c353ce-0bfa-4672-9caf-f1bac670bbad', '2021-01-21 03:45:32', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Xayah', '/images/champions/set4-5/xayah.png', '/images/champions-bg/set4-5/xayah.png', '20', '/images/champion-skills/set4-5/xayah_featherstorm.png', '暴风羽刃', '主动', '{\"Consume\": \"70/130\", \"Attack Damage\": \"250%/275%/325%\", \"Magic Damage Per Feather\": \"100/200/400\"}', '霞', '4', '600/1080/1944', '80/144/259.2', '68/122/220', '3', '0.85', '20', '霞跃向空中，期间变为不可被选取状态、免疫伤害并呈扇形掷出9支羽刃，对途经的敌人们造成相当于她250/275/325%攻击力的物理伤害。\r\n霞随后会召回她的羽刃，敌人每被一支羽刃命中就会受到100/200/400魔法伤害。\r\n在技能施放持续时间后的法力锁定：1.5秒', '逆羽');
INSERT INTO `champion` VALUES ('f5061f4f-a2ae-48de-9e27-95b553e367f9', '2021-01-21 03:45:31', '2021-01-30 06:42:15', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Rakan', '/images/champions/set4-5/rakan.png', '/images/champions-bg/set4-5/rakan.png', '40', '/images/champion-skills/set4-5/rakan_disarmingdiversion.png', '盛大登场', '主动', '{\"Consume\": \"40/80\", \"Shield Amount\": \"300/550/950\", \"Disarm Duration\": \"2.5/3/4seconds\"}', '洛', '2', '600/1080/1944', '60/108/194.4', '42/76/136', '2', '0.7', '40', '洛冲刺向距离内相距最远的敌人，使沿途的所有单位缴械2.5/3/4秒，并嘲讽他的目标。洛随后会给自己生成一个拥有200/400/800生命值的护盾，持续4秒。', '幻翎');
INSERT INTO `champion` VALUES ('f542c032-ec91-48f4-8877-5d06c272f582', '2021-01-21 03:45:30', '2021-01-30 06:42:14', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Aurelion Sol', '/images/champions/set4-5/aurelionsol.png', '/images/champions-bg/set4-5/aurelionsol.png', '80', '/images/champion-skills/set4-5/aurelionsol_voiceoflightning.png', '霹雳高落', '主动', '{\"Damage\": \"350/550/1750\", \"Consume\": \"0/80\"}', '奥瑞利安·索尔', '4', '650/1170/2106', '40/72/129.6', '30/54/97', '4', '0.75', '20', '奥瑞利安 · 索尔沿直线朝相距最远的敌人进行闪电吐息，造成325/500/1750魔法伤害并使命中的所有敌人都【过量充电】10秒。\r\n如果一名敌人已被【过量充电】，那么奥瑞利安 · 索尔会消耗该效果并造成50%额外伤害。', '铸星龙王');
INSERT INTO `champion` VALUES ('fa0721a1-2129-4cd4-98dd-f6c6eaf804fb', '2021-01-21 03:45:31', '2021-01-30 06:42:11', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Teemo', '/images/champions/set4-5/teemo.png', '/images/champions-bg/set4-5/teemo.png', '20', '/images/champion-skills/set4-5/teemo_sporeclouddart.png', '孢子云吹箭', '主动', '{\"Damage\": \"250/400/900\", \"Consume\": \"0/60\", \"Duration\": \"2.5/3/4.5\"}', '提莫', '2', '500/900/1620', '55/99/178.2', '39/69/125', '3', '0.7', '20', '提莫朝攻击速度最高的敌人发射一根吹箭，吹箭在接触到敌人时会爆炸成一团孢子云，使附近的敌人中毒。中毒的敌人会在2.5/3/5秒里持续受到共250/400/900魔法伤害并在持续时间内被致盲。', '迅捷斥候');
INSERT INTO `champion` VALUES ('fea2c3d6-fd71-4174-9f30-b2fe957b9444', '2021-01-21 03:45:32', '2021-01-30 06:42:13', 0, '63fc5265-4c6b-465b-8928-c7a0d54a9010', 'Sejuani', '/images/champions/set4-5/sejuani.png', '/images/champions-bg/set4-5/sejuani.png', '40', '/images/champion-skills/set4-5/sejuani_firecracker.png', '烈焰爆竹', '主动', '{\"Damage\": \"100/200/800\", \"Consume\": \"80/160\", \"Stun Duration\": \"2.5/3/6\"}', '瑟庄妮', '4', '1000/1800/3240', '45/81/145.8', '25/45/80', '1', '0.55', '60', '瑟庄妮朝相距最近的敌人扔出一个巨大的爆竹，爆竹会在2秒的延迟后爆炸。爆炸范围内的敌人会受到100/200/800魔法伤害和2.5/3/6秒晕眩效果。', '北地之怒');
